ll arrceil(vll a,ll k)
{
    ll n=a.size();
    ll low=0;
    ll high=n-1;

    if(a[0]>k)
        return -1;
    while(low<high)

    {
       // debug(low,high);
        ll mid=(low+high+1)/2;
        if(a[mid]<=k)
            low=mid;
        else
            high=mid-1;
    }
    return low;
}

