vector<node*> child[max0 + 5];

node *p[maxn + 5];

node *rt;

int dfn[max0 + 5], End[max0 + 5];
int tot = 0;

node *add(node *&rt, char *s)
{
 if (rt == NULL) rt = nd + (cur++);
 if (!s[0]) return rt;
 node *tmp = add(rt->go[get(s[0])], s + 1);
 rt->go[get(s[0])]->p = get(s[0]);
 return tmp;
}

void dfs(node *rt)
{
 int x = rt - nd;
 dfn[x] = tot++;
 for (auto y : child[rt - nd]) dfs(y);
 End[x] = tot;
}

int c[max0 + 5];

inline void add(const int &x, const int &y)
{
 for (int i = x + 1; i <= max0; i += i & -i) c[i] += y;
}

inline int query(const int &x)
{
 int ans = 0;
 for (int i = x; i > 0; i -= i & -i) ans += c[i];
 return ans;
}

int n;

bool f[maxn + 5][maxn + 5];

int pos[maxn + 5], len[maxn + 5];

inline bool cmp(int x, int y)
{
 return len[x] < len[y];
}

int link[maxn + 5];
bool vis[maxn + 5];

inline bool find(int x)
{
 REP(y, 0, n)
  if (f[x][y] && !vis[y])
  {
   vis[y] = 1;
   if (link[y] == -1 || find(link[y]))
   {
    link[y] = x;
    return 1;
   }
  }
 return 0;
}

