  p[i] = add(rt, s);
 }
 sort(pos, pos + n, cmp);
 static node *q[max0 + 5];
 int head = 0, rear = 0;
 q[rear++] = rt;
 rt->f = rt;
 while (head != rear)
 {
  node *x = q[head++];
  for (int i = 0; i < 2; ++i)
  {
   node *y = x->go[i];
   if (!y) 
   {
    x->go[i] = x == rt ? rt : x->f->go[i];
   }
   else
   {
    q[rear++] = y;
    y->f = x == rt ? x : x->f->go[y->p];
   }
  }
 }
 REP(i, 1, rear) child[q[i]->f - nd].pb(q[i]);
 dfs(rt);
 REP(i, 0, cur) nd[i].f = NULL;
