   int tmp = p[pos[j]] - nd;
   f[i][j] = query(End[tmp]) - query(dfn[tmp]);
  }
  now = p[pos[i]];
  while (now)
  {
   add(dfn[now - nd], -1);
   now = now->f;
  }
 }
 memset(link, -1, sizeof link);
