int dfs(int u)
{
 int lowu=pre[u]=++clo;
 s.push(u);
 for (int v:G[u])
  if (!pre[v])
   lowu=min(lowu,dfs(v));
  else if (!sccno[v])
   lowu=min(lowu,pre[v]);
