#include <bits/stdc++.h>

#define pq priority_queue<ll>

typedef long long ll;

using namespace std;

pq generateOrig(ll n) {
    ll power = 0;
    pq q;
    while(n > 0) {
        if(n % 2 == 0) {
            n /= 2;
            power++;
        }
        else {
            n--;
            n /= 2;
            q.push(power);
            power++;
        }
    }
    return q;
}

pq reduce(pq & q, ll k) {
    ll len = q.size();
    for(ll i = len; i < k; i++) {
        ll curr = q.top(); q.pop();
        q.push(curr-1); q.push(curr-1);
    }
    return q;
}

ll printSet(pq & q) {
    ll ret = 0;
    //cout << "size:  " << q.size() << "\n";
    while(!q.empty()) {
        ll print = q.top(); q.pop();
        ret += 1LL << print;
        cout << print << " ";
    }
    cout << "\n";
    return ret;
}

int main() {
    //freopen("B.in", "r", stdin);
    //freopen("B.out", "w", stdout);
    ll n, k; cin >> n >> k;
    pq q = generateOrig(n);
    q = reduce(q, k);
    ll len = q.size();
    if(len == k) {
        cout << "Yes\n";
        ll sm = printSet(q);
        //cout << sm << "\n";
    } else {
        cout << "No\n";
    }

}

