#include<bits/stdc++.h>
using namespace std;
const int MOD=1e9+7;
char n[1234];
int dex[1234],base[1234],k;
vector<int>V[1234];
long long dp[1234][1234][2];
long long solve(int now,int rest,int ok) {
 if(now<0)return rest==0;
 if(ok&&dp[now][rest][ok]!=-1)return dp[now][rest][ok];
 long long a1=0,a2=0;
 if(rest&&(ok||n[now]=='1'))a1=solve(now-1,rest-1,ok)%MOD;
 a2=solve(now-1,rest,ok||n[now]=='1')%MOD;
 return dp[now][rest][ok]=(a1+a2)%MOD;
}
int fix(int x) {
 int ret=0;
 while(x) {
  if(x&1)ret++;
  x>>=1;
 }
 return ret;
}
int dfs(int x) {
 if(x==1)return 0;
 return dfs(dex[x])+1;
}
int main() {
 ios::sync_with_stdio(false);
 cin.tie(0);
 cout.tie(0);
 cin>>n>>k;
 if(k==0) {
  cout<<1<<endl;
  return 0;
 }
 for(int i=1; i<=1000; i++)dex[i]=fix(i);
 for(int i=1; i<=1000; i++)base[i]=dfs(i);
 for(int i=1; i<=1000; i++)V[base[i]].push_back(i);
 long long ans=0;
 int len=strlen(n);
 reverse(n,n+len);
 memset(dp,-1,sizeof(dp));
 for(auto it:V[k-1]) {
  if(it>len)continue;
  if(k==1)ans--;
  ans=(ans+solve(len-1,it,0))%MOD;
 }
 cout<<ans<<endl;
 return 0;
}

#include <iostream>
