using namespace std;

struct EnemyInfo
{
 int max_health;
 int start_health;
 int regen;
};

struct HealthUpdate
{
 int start[100];
 int end[100];
 int m = 0;
};

int Checking(HealthUpdate Health[], int TimeStone, int n);

int main() {

 EnemyInfo Enemy[1000];
 HealthUpdate Heal[1000];

 int n, m, time, enemy, health;
 cin >> n >> m;
 int TimeStone[1000];
 int stone = 0;
 int max = 0, ans;

 TimeStone[stone++] = 0;

 int bounty, increase, damage;
 cin >> bounty >> increase >> damage;
 for (int i = 0; i < n; i++) {

  cin >> Enemy[i].max_health >> Enemy[i].start_health >> Enemy[i].regen;

  if (Enemy[i].max_health < damage)
  {
   cout << "-1" << endl;
   return 0;
  }

  Heal[i].start[Heal[i].m] = 0;

  if (Enemy[i].start_health >= damage)
   Heal[i].end[Heal[i].m] = 0;
  else Heal[i].end[Heal[i].m] = (damage - Enemy[i].start_health) / Enemy[i].regen;

  ++Heal[i].m;

  TimeStone[stone++] = Heal[i].end[Heal[i].m - 1];
 }
 for (int i = 0; i < m; i++)
 {
  cin >> time >> enemy >> health;

  if (health > Enemy[enemy - 1].max_health) continue;

  Heal[enemy - 1].start[Heal[enemy - 1].m] = time;

  TimeStone[stone++] = time;

  if (Heal[enemy - 1].end[Heal[enemy - 1].m - 1] >= time) {
   Heal[enemy - 1].end[Heal[enemy - 1].m - 1] = time - 1;
   TimeStone[stone - 1] = time - 1;
  }

  Heal[enemy - 1].end[Heal[enemy - 1].m] = time + (damage - health) / Enemy[enemy - 1].regen;

  TimeStone[stone++] = Heal[enemy - 1].end[Heal[enemy - 1].m];

  ++Heal[enemy - 1].m;
 }

 for (int i = 0; i < stone; i++)
 {
  ans = Checking(Heal, TimeStone[i], n)*(bounty + TimeStone[i] * increase);
  if (ans > max) max = ans;
 }

 cout << max << endl;

}

int Checking(HealthUpdate Health[], int TimeStone, int n) {

 int count = 0;

 for (int i = 0; i < n; i++)
 {
  for (int j = 0; j < Health[i].m; j++)
  {
   if ((TimeStone >= Health[i].start[j]) && (TimeStone <= Health[i].end[j]))
   {
    ++count;
    break;
   }
  }
 }

 return count;
}

