using namespace std;

int n,T,Sum[Vmax],Cnt[Vmax];

struct pii
{
    int fi,se,ind;
    bool operator < (const pii &A) const
    {
        return fi < A.fi;
    }
} prob[Nmax],aux[Nmax];

void upd(int p)
{
    ++Cnt[p];
    int ind = p/100 + ((p%100)>0);
    Sum[ind] += p;
}

int qry(int S)
{
    int i,j,ans=0;
    for(i=1;i<=100;++i)
        if(Sum[i]<=S)
        {
            S-=Sum[i];
            ans+=Cnt[i];
        }
    if(i<=100 && S)
    {
        for(j=(i-1)*100+1;j<(i+1)*100;++j)
        {
            int mn = min(Cnt[j],S/j);
            ans+=mn;
            S-=mn*j;
        }
    }
    return ans;
}

