            ll x=q[v].top();q[v].pop();
            if(tt[u]+x<=m){
                tt[u]+=x;
                q[u].push(x);
            }
            else if(x<q[u].top()){
                tt[u]-=q[u].top();
                tt[u]+=x;q[u].pop();q[u].push(x);
            }
        }
    }
    ans=max(ans,node[u]*(int)q[u].size());
}
int main(){
    ios_base::sync_with_stdio(false);cin.tie(NULL);
    int n;cin>>n>>m;
    int st;
    for(int i=1;i<=n;i++){
        int b;cin>>b>>cst[i]>>node[i];
        if(!b)st=i;
        else g[i].pb(b),g[b].pb(i);
    }
    dfs(st,st);cout<<ans;
}

