void update(int p, int l, int r, int i, int v, int t){
    if (l == r)
        tree[t][p] += v;

    else{
        int mid = (l+r)/2;
        if (i <= mid) update(2*p, l, mid, i, v, t);
        else update(2*p+1, mid+1, r, i, v, t);
        tree[t][p] = tree[t][2*p]+tree[t][2*p+1];
    }
}

int query(int p, int l, int r, int i, int j, int t){
    if (i <= l && r <= j)
        return tree[t][p];
    if (r < i || j < l)
        return 0;

    int mid = (l+r)/2;
    return query(2*p, l, mid, i, j, t)+query(2*p+1, mid+1, r, i, j, t);
}

int bs(int t, int l, int r){
    int ans = -1, mx = query(1, 1, n+1, l, n+1, t);

    while (l <= r){
        int mid = (l+r)/2;
        if (query(1, 1, n+1, mid, n+1, t) < mx){
            ans = mid-1;
            r = mid-1;
        }
        else
            l = mid+1;
    }

    return ans;
}

