const int N = 3e5*2 + 5;
ll tree[4 * N],cpy[4*N], lazy[4 * N] = { 0 };
int val[N];
void probagate(int l, int r, int p) {
 //tree[p] -= lazy[p];
 if(lazy[p]==0)return ;
 if(lazy[p]==1){
        tree[p]=cpy[p];
 }
 else if(lazy[p]==-1) {
        cpy[p]=tree[p];
        tree[p]=0;
 }
 if (l != r) {
  lazy[p << 1] += lazy[p];
  lazy[(p << 1) | 1] += lazy[p];
 }
 lazy[p] = 0;
}
void build(int l, int r, int p) {
 if (l == r) {
  tree[p] = val[l];
  lazy[p] = 0;
  return;
 }
 int m = (l + r) >> 1;
 build(l, m, (p << 1));
 build(m + 1, r, (p << 1) | 1);
 tree[p] = tree[(p << 1)] + tree[(p << 1) | 1];
 cpy[p]=tree[p];
 lazy[p] = 0;
 return;
}
ll get(int ql, int qr, int l, int r, int p) {
 probagate(l, r, p);
 if (ql > r || qr < l)
  return 1e18;
 if (ql <= l && r <= qr)
  return tree[p];
 int m = (l + r) >> 1;
 return min(get(ql, qr, l, m, p << 1), get(ql, qr, m + 1, r, (p << 1) | 1));
}
void update(ll val, int ql, int qr, int l, int r, int p) {
 probagate(l, r, p);
 if (ql > r || qr < l)
  return;
  //  cout<<val<<" "<<q1<<" "<<qr<<" "<<l<<" "<<r<<" "<<p<<endl;
 if (ql <= l && r <= qr) {
        if(lazy[p]==val)
            return;
  lazy[p] = val;
  probagate(l, r, p);
  return;
 }
 int m = (l + r) >> 1;
 update(val, ql, qr, l, m, (p << 1));
 update(val, ql, qr, m + 1, r, (p << 1) | 1);
 tree[p] = tree[(p << 1)] + tree[(p << 1) | 1];
}


