    }
    sort(o+1,o+len+1);len=unique(o+1,o+len+1)-o-1;
    for (register int i=1;i<=n;++i) a[i]=lower_bound(o+1,o+len+1,a[i])-o;
    for (register int i=1;i<=cnt2;++i){
        q2[i].val=lower_bound(o+1,o+len+1,q2[i].val)-o;
        q2[i].pre=lower_bound(o+1,o+len+1,q2[i].pre)-o;
    }
    sort(q1+1,q1+cnt1+1);
    int L=1,R=0,X=1;num[0]=1e9;t[0]=1;
