        while (X>q1[i].t) change(q2[X].pos,q2[X].pre),--X;
        while (L>q1[i].l) update(--L);
        while (R<q1[i].r) update(++R);
        while (L<q1[i].l) update(L++);
        while (R>q1[i].r) update(R--);
        ans[q1[i].id]=calc();
    }
    for (register int i=1;i<=cnt1;++i) printf("%d\n",ans[i]);
    return 0;
}

