struct DSU {

    int *parent, *sizeRootedAt;

    DSU(int sz) {
        parent = new int[sz];
        sizeRootedAt = new int[sz];
        memset(sizeRootedAt, 0, sz * sizeof(int));
        F0R(i, sz) parent[i] = i;
    }

    int root(int a) {
        if(parent[a] == a) return a;
        return (parent[a] = root(parent[a]));
    }

    void join(int a, int b) {
        int ra = root(a), rb = root(b);
        if(ra == rb) return;
        parent[ra] = rb;
        sizeRootedAt[rb] += sizeRootedAt[ra];
    }

    int sizeWith(int a) {
        return sizeRootedAt[root(a)];
    }

};

