struct elem {
    int a;
    int t;
    int id;

    bool operator < (const elem& T) const {
        return a < T.a;
    }
};

int n, T;
vector<elem> a;

inline void init() {
    scanf("%d%d", &n, &T);
    a.resize(n);
    for (int i = 0; i < n; ++i) {
        scanf("%d%d", &a[i].a, &a[i].t);
        a[i].id = i;

    }
    sort(a.rbegin(), a.rend());
}

int qsum;
int qsum2;
vector<bool> inq;
vector<bool> inq2;

int sz2;
priority_queue<pii> q;
priority_queue<pii> q2;
priority_queue<pii, vector<pii>, greater<pii> > q3;

vector<bool> cur_inq;
vector<bool> ans_inq;

vector<bool> cur_inq2;
vector<bool> ans_inq2;

inline void add_to_queue(int ind) {
    if (inq2[ind]) {
        qsum2 -= a[ind].t;
        --sz2;
        inq2[ind] = false;
    }
    qsum += a[ind].t;
    inq[ind] = true;
    q.push(pii(a[ind].t, ind));
}

inline void norm_queue(int score) {
    while (static_cast<int>(q.size()) > score) {
        pii cur = q.top();
        q3.push(cur);
        q.pop();
        qsum -= cur.first;
        inq[cur.second] = false;
    }
}

inline void update_state(int lim) {
    while (sz2 > lim) {
        pii cur = q2.top();
        q2.pop();
        if (inq[cur.second]) continue;
        if (!inq2[cur.second]) continue;
        --sz2;
        qsum2 -= cur.first;
        inq2[cur.second] = false;
        q3.push(cur);
    }
    while (sz2 < lim) {
        pii cur3 = q3.top();
        q3.pop();
        if (inq[cur3.second]) continue;
        if (inq2[cur3.second]) continue;
        ++sz2;
        qsum2 += cur3.first;
        inq2[cur3.second] = true;
        q2.push(cur3);
    }
    while (q3.size() && q2.size()) {
        if (inq[q2.top().second]) {
            q2.pop();
            continue;
        }
        if (inq[q3.top().second]) {
            q3.pop();
            continue;
        }
        if (!inq2[q2.top().second]) {
            q2.pop();
            continue;
        }
        if (q3.top().first >= q2.top().first) {
            break;
        }
        pii cur = q2.top();
        pii cur3 = q3.top();
        q2.pop();
        q3.pop();
        --sz2;
        qsum2 -= cur.first;
        inq2[cur.second] = false;
        q3.push(cur);
        q2.push(cur3);
        ++sz2;
        qsum2 += cur3.first;
        inq2[cur3.second] = true;
    }
}

inline int check(int score) {
    while (!q.empty()) q.pop();
    while (!q2.empty()) q2.pop();
    while (!q3.empty()) q3.pop();

    for (int i = 0; i < n; ++i) {
        q3.push(pii(a[i].t, i));
    }

    inq.assign(n, false);
    inq2.assign(n, false);
    sz2 = 0;
    qsum = 0;
    qsum2 = 0;
    int ind = 0;
    for (int k = n; k >= score; --k) {
        while (ind < n && a[ind].a >= k) {
            add_to_queue(ind);
            norm_queue(score);
            ++ind;
        }
        if (static_cast<int>(q.size()) != score) {
            continue;
        }
        if (qsum > T) continue;
        update_state(k - score);
        if (qsum + qsum2 <= T) {
            cur_inq = inq;
            cur_inq2 = inq2;
            return 1;
        }
    }

    return 0;
}

int main() {
    // freopen("input.txt", "r", stdin); freopen("output.txt", "w", stdout);

    init();

    ans_inq.assign(n, false);
    ans_inq2.assign(n, false);

    int res = 0;
    int l = 1, r = n;
    while (l <= r) {
        int mid = (l + r) >> 1;
        if (check(mid)) {
            res = mid;
            ans_inq = cur_inq;
            ans_inq2 = cur_inq2;
            l = mid + 1;
        } else {
            r = mid - 1;
        }
    }

    printf("%d\n", res);
    vector<int> ans;
    for (int i = 0; i < n; ++i) {
        if (!ans_inq[i] && !ans_inq2[i]) continue;
        ans.push_back(a[i].id + 1);
    }

    int k = static_cast<int>(ans.size());
    printf("%d\n", k);
    for (int i = 0; i < k; ++i) {
        printf("%d ", ans[i]);
    }
    printf("\n");

    return 0;
}

