int N, q, e;
int b[600005];
int reel[600005];
unordered_map<int, int> fake;
set<int> comp;
struct SegmentTreeLazy {
  vector<int> st, lazy;
  SegmentTreeLazy(int _N) {
    st.resize(4*_N);
    lazy.assign(4*_N, -1);                     // Assign All
//   lazy.assign(4*_N, 0);                      // Add to All
    build();
  }
  int op(int l, int r) {
    return l+r;                                       // Sum
//  return l==-1?r:r==-1?l:max(l, r);                 // Max
//  return l==-1?r:r==-1?l:min(l, r);                 // Min
  }
  void build(int id=1,int l=0,int r=N) {
    if(l+1 == r) {
      st[id] = 1;
      return;
    }
    int mid = (l+r)/2;
    build(id*2  , l, mid);
    build(id*2+1, mid, r);
    st[id] = op(st[id*2], st[id*2+1]);
  }
  void upd(int id, int l, int r, int val) {
    lazy[id] = val; // Update Lazy             // Assign All
//  lazy[id] += val;                           // Add To All
//  if(lazy[id] == -1) return;                 // Assign All
    if(l+1 == r) // Update value of b[l]
      b[l] = val;                              // Assign All
//    b[l] += val;                             // Add to All
//  st[id] = val; // Update current st[id]    Assign Min/Max
    st[id] = val*(reel[r]-reel[l]);                        // Assign Sum
//    cerr << l << " " << r << " " << st[id] << endl;
//    cerr << reel[l] << " " << reel[r] << " " << st[id] << endl;
//  st[id] += val;                         // Add to Min/Max
//  st[id] += val*(r-l);                       // Add to Sum
  }
  void shift(int id, int l, int r) {
  if(lazy[id]==-1) return;                   // Assign All
    int mid = (l+r)/2;
    upd(id*2  , l, mid, lazy[id]);
    upd(id*2+1, mid, r, lazy[id]);
  lazy[id] = -1;                             // Assign All
//    lazy[id] = 0;                              // Add To All
  }
  void modify(int x,int y,int val,int id=1,int l=0,int r=N){
    if(x >= r || y <= l) return;
    if(x <= l && y >= r) {
      upd(id, l, r, val);
      return;
    }
    shift(id, l, r);
    int mid = (l+r)/2;
    modify(x, y, val, id*2  , l, mid);
    modify(x, y, val, id*2+1, mid, r);
    st[id] = op(st[id*2], st[id*2+1]);
  }
  int query(int x, int y, int id=1,int l=0,int r=N) {
    if(x >= r || y <= l) return 0;                    // Sum
//  if(x >= r || y <= l) return -1;               // Min/Max
    if(x <= l && y >= r) return st[id];
    shift(id, l, r);
    int mid = (r+l)/2;
    return op(query(x, y, id*2  , l, mid),
              query(x, y, id*2+1, mid, r));
  }
};

signed main() {
  ios_base::sync_with_stdio(false);
  cin.tie(NULL);
  int seed;
  asm("rdtsc" : "=A"(seed));
  srand(seed);
  e = rand();
  fake.reserve(32768); fake.max_load_factor(0.25);

  cin >> N >> q;
  int actN = N;

  comp.insert(0);
  comp.insert(N);
  vector<pair<int, pair<int, int>>> op;
  for(int i = 0; i < q; i++) {
    int k, l, r;
    cin >> l >> r >> k;
    l--;
    comp.insert(l);
    comp.insert(r);
    op.push_back({k, {l, r}});
  }

  int i = 0;
  auto it = comp.begin();
  while(it != comp.end()) {
    reel[i] = *(it);
    fake[e^*(it)] = i;
    it++;
    i++;
  }
  cerr << endl;

  N = comp.size()-1;
  SegmentTreeLazy st(N);
  st.modify(0, N, 1);

  for(int i = 0; i < q; i++) {
    int k, l, r;
    k = op[i].first;
    l = op[i].second.first;
    r = op[i].second.second;

 //   cerr << l << " " << r << endl;
 //   cerr << fake[e^l] << " " << fake[e^r] << endl;
    st.modify(fake[e^l], fake[e^r], k-1);
    cout << st.query(0, N) << endl;
  }

}

