bool cmp(const REQ&ta, const REQ&tb) { return ta.nowchange != tb.nowchange ? ta.nowchange < tb.nowchange : ta.l / p != tb.l / p ? ta.l / p < tb.l / p : ta.r / p < tb.r / p; }

void update(int pos, int mode) {
 --sz[cnt[pos]]; cnt[pos] += mode; ++sz[cnt[pos]];
}
void chn(int now, int l, int r)
{
 if (l <= change[now].pos&&r >= change[now].pos)update(a[change[now].pos], -1);
 swap(a[change[now].pos], change[now].v);
 if (l <= change[now].pos&&r >= change[now].pos)update(a[change[now].pos], 1);
}

int wls(){
 int t = 0;
 while (++t)if (sz[t] == 0)return t;
}

void gao()
{
 int l = 1, r = 1, now = 0; update(1, 1);
 for (auto it : req)
 {
  for (; r < it.r; ++r)update(a[r + 1], 1);
  for (; r > it.r; --r)update(a[r], -1);
  for (; l < it.l; ++l)update(a[l], -1);
  for (; l > it.l; --l)update(a[l - 1], 1);
  for (; now < it.nowchange; ++now)chn(now, l, r);
  ans[it.id] = wls();
 }
}

