void dfs(int x, int y)
{
 if(y > Ans) return;
 if(x == N)
 {
  Ans = y;
  return;
 }
 if(x + 1 <= N) dfs(x + 1, y + A);
 if(x * K <= N) dfs(x * K, y + B);
}

