pair<int,int> tree[2][2][mxN<<2];

int N;
void upd(int d,int ind,int val,int n = 1,int l = 0,int r = N) {
 if(l == r) {
  tree[d][0][n] = {val,ind * (d?-1:1)};
  tree[d][1][n] = {val,ind * (d?-1:1)};
  return;
 }
 int md = (l + r)>>1;
 if(ind <= md) upd(d,ind,val,n<<1,l,md);
 else upd(d,ind,val,n<<1|1,md+1,r);
 tree[d][0][n] = min(tree[d][0][n<<1],make_pair(tree[d][0][n<<1|1].first + (md-l+1),tree[d][0][n<<1|1].second));
 tree[d][1][n] = min(make_pair(tree[d][1][n<<1].first+(r-md),tree[d][1][n<<1].second),tree[d][1][n<<1|1]);
}

pair<int,int> get(int d,int f,int a,int b,int n = 1,int l = 0,int r = N) {
 if(a == l && b == r) return tree[d][f][n];
 int md = (l+r)>>1;
 if(b <= md) return get(d,f,a,b,n<<1,l,md);
 if(a > md) return get(d,f,a,b,n<<1|1,md+1,r);
 if(f == 0) {
  auto curr = get(d,f,md+1,b,n<<1|1,md+1,r);
  curr.first += (md - a + 1);
  return min(get(d,f,a,md,n<<1,l,md),curr);
 }
 else {
  auto curl = get(d,f,a,md,n<<1,l,md);
  curl.first += (b - md);
  return min(curl,get(d,f,md+1,b,n<<1|1,md+1,r));
 }
}

