  pair<int,int> mnl;
  mnr.second *= -1;
  if(mnr.second <= i) mnl = get(0,1,0,mnr.second);
  else mnl = min(get(0,0,i,min(mnr.second,N-1)),get(0,1,0,i));
  time = min(time,max(mnr.first,mnl.first));
 }
 if(time >= inf) cout<<"-1\n";
 else cout<<time<<'\n';
 return 0;
}

