    LL num=LCM/k[1]+LCM/k[2]+LCM/k[3]
        -(gcd(k[1],k[2])==1?LCM/lcm(k[1],k[2]):0)
        -(gcd(k[3],k[2])==1?LCM/lcm(k[3],k[2]):0);
    if(gcd(k[1],k[3])<=2)num-=LCM/lcm(k[1],k[3]);
    if(gcd(k[1],k[3])<=2&&gcd(k[1],k[2])==1&&gcd(k[2],k[3])==1)++num;
    if(num==LCM){printf("YES\n");return 0;}
    num=LCM/k[1]+LCM/k[2]+LCM/k[3]
        -(gcd(k[1],k[2])==1?LCM/lcm(k[1],k[2]):0)
        -(gcd(k[3],k[1])==1?LCM/lcm(k[3],k[1]):0);
    if(gcd(k[2],k[3])<=2)num-=LCM/lcm(k[2],k[3]);
    if(gcd(k[1],k[3])==1&&gcd(k[1],k[2])==1&&gcd(k[2],k[3])<=2)++num;
    if(num==LCM)printf("YES\n");
    else printf("NO\n");
    return 0;
}

