#include <bits/stdc++.h>
#define pb push_back
#define mp make_pair
#define mod 1000000007

using namespace std;

typedef long long ll;

#define pii pair<ll, ll>

ll n, seg[300001], seg3[300001], seg4[300001], a[100001], diff[100001], localMax[100001] = {0}, frwdDiff[100001];

pii seg2[300001];

ll ans = 0;

ll lazy[300001] = {0};

void build(ll l, ll r, ll i){
    if(l == r){
        seg[i] = diff[l];
        seg2[i].first = l;
        seg2[i].second = a[l];
        seg3[i] = localMax[l];
        seg4[i] = frwdDiff[l];
        return;
    }
    ll mid = (l+r)/2;
    build(l, mid, i*2+1);
    build(mid+1, r, i*2+2);

    seg[i] = max(seg[i*2+1], seg[i*2+2]);
    seg3[i] = seg3[i*2+1] | seg3[i*2+2];
    seg4[i] = max(seg4[i*2+1], seg4[i*2+2]);

    seg2[i] = seg2[i*2+1];
    if(seg2[i].second > seg2[i*2+2].second) seg2[i] = seg2[i*2+2];
}

ll query(ll l, ll r, ll i, ll x, ll y){
    if(r < x || l > y) return -mod;
    if(l >= x && r <= y) return seg[i];
    ll mid = (l+r)/2;
    return max(query(l, mid, i*2+1, x, y), query(mid+1, r, i*2+2, x, y));
}

ll query3(ll l, ll r, ll i, ll x, ll y){
    if(r < x || l > y) return 0;
    if(l >= x && r <= y) return seg3[i];
    ll mid = (l+r)/2;
    return (query3(l, mid, i*2+1, x, y) | query3(mid+1, r, i*2+2, x, y));
}

ll query4(ll l, ll r, ll i, ll x, ll y){
    if(r < x || l > y) return -mod;
    if(l >= x && r <= y) return seg4[i];
    ll mid = (l+r)/2;
    return max(query4(l, mid, i*2+1, x, y), query4(mid+1, r, i*2+2, x, y));
}

void update(ll l, ll r, ll i, ll pos){
    if(l == r){
        seg[i] = diff[l];
        return;
    }
    ll mid = (l+r)/2;
    if(mid >= pos) update(l, mid, i*2+1, pos);
    else update(mid+1, r, i*2+2, pos);
    seg[i] = max(seg[i*2+1], seg[i*2+2]);
}

void update_lazy(ll l, ll r, ll i){
    if(!lazy[i]) return;
    if(l != r){
        lazy[i*2+1] += lazy[i];
        lazy[i*2+2] += lazy[i];
    }
    else a[l] += lazy[i];
    seg2[i].second += lazy[i];
    lazy[i] = 0;
}

void update2(ll l, ll r, ll i, ll x, ll y, ll val){
    update_lazy(l, r, i);
    if(r < x || l > y) return;
    if(l >= x && r <= y){
        lazy[i] = val;
        update_lazy(l, r, i);
        return;
    }
    ll mid = (l+r)/2;
    update2(l, mid, i*2+1, x, y, val);
    update2(mid+1, r, i*2+2, x, y, val);
    if(l != r){
        seg2[i] = seg2[i*2+1];
        if(seg2[i].second > seg2[i*2+2].second) seg2[i] = seg2[i*2+2];
    }
}

pii query2(ll l, ll r, ll i, ll x, ll y){
    update_lazy(l, r, i);
    if(r < x || l > y) return mp(-1, mod);
    if(l >= x && r <= y) return seg2[i];
    ll mid = (l+r)/2;
    pii left = query2(l, mid, i*2+1, x, y);
    pii rgt = query2(mid+1, r, i*2+2, x, y);

    if(l != r){
        seg2[i] = seg2[i*2+1];
        if(seg2[i].second > seg2[i*2+2].second) seg2[i] = seg2[i*2+2];
    }

    if(left.second == mod) return rgt;
    if(rgt.second == mod) return left;
    if(left.second > rgt.second) return rgt;
    return left;
}

void update3(ll l, ll r, ll i, ll pos){
    if(l == r){
        seg3[i] = localMax[l];
        return;
    }
    ll mid = (l+r)/2;
    if(mid >= pos) update3(l, mid, i*2+1, pos);
    else update3(mid+1, r, i*2+2, pos);
    seg3[i] = seg3[i*2+1] | seg3[i*2+2];
}

void update4(ll l, ll r, ll i, ll pos){
    if(l == r){
        seg4[i] = frwdDiff[l];
        return;
    }
    ll mid = (l+r)/2;
    if(mid >= pos) update4(l, mid, i*2+1, pos);
    else update4(mid+1, r, i*2+2, pos);
    seg4[i] = max(seg4[i*2+1], seg[i*2+2]);
}

ll query_(ll l, ll r, ll x){
    if(l == r){
        query2(0, n-1, 0, l-1, l-1);
        query2(0, n-1, 0, l+1, l+1);
        query2(0, n-1, 0, l, l);
        ll prv = abs(a[l] - a[l-1]) + abs(a[l] - a[l+1]);
        ll t = abs(a[l] + x - a[l-1]) + abs(a[l] + x - a[l+1]);
        return (t - prv);
    }
    if(l+1 == r){
        query2(0, n-1, 0, l-1, l-1);
        query2(0, n-1, 0, l+1, l+1);
        query2(0, n-1, 0, l, l);
        query2(0, n-1, 0, r+1, r+1);

        ll prv1 = abs(a[l] - a[l-1]) + abs(a[l] - a[l+1]), prv2 = abs(a[r] - a[r+1]) + abs(a[r] - a[r-1]);
        ll t1 = abs(a[l]+x - a[l-1]) + abs(a[l]+x - a[l+1]), t2 = abs(a[r] + x - a[r+1]) + abs(a[r]+x - a[r-1]);
        return max(t1 - prv1, t2 - prv2);
    }

    ll hasMax = query3(0, n-1, 0, l, r);
    if(hasMax) return (x * 2);
    ll minPos = query2(0, n-1, 0, l, r).first;

    if(minPos == l){
        ll t = query(0, n-1, 0, l, r);
        return max((ll)0, (t + x)*2);
    }
    if(minPos == r){
        ll t = query4(0, n-1, 0, l, r);
        return max((ll)0, (t+x)*2);
    }
    ll t = query4(0, n-1, 0, l, minPos - 1);
    t = max(t, query(0, n-1, 0, minPos + 1, r));

    a[minPos - 1] = query2(0, n-1, 0, minPos-1, minPos-1).second;
    a[minPos] = query2(0, n-1, 0, minPos, minPos).second;
    a[minPos + 1] = query2(0, n-1, 0, minPos+1, minPos+1).second;

    ll a1 = a[minPos - 1], b = a[minPos], c = a[minPos + 1];
    ll val = max((ll)0, x - (c - b)) - min(x, (a1 - b));
    val *= 2;
    ll val2 = max((t + x)*2, (ll)0);
    return max(val, val2);
}

void update_(ll l, ll r, ll val){
    a[l-1] = query2(0, n-1, 0, l-1, l-1).second;
    a[l] = query2(0, n-1, 0, l, l).second;
    a[l+1] = query2(0, n-1, 0, l+1, l+1).second;
    a[r+1] = query2(0, n-1, 0, r+1, r+1).second;
    a[r] = query2(0, n-1, 0, r, r).second;
    a[r-1] = query2(0, n-1, 0, r-1, r-1).second;

    ans -= abs(a[l-1] - a[l]);
    ans -= abs(a[r] - a[r+1]);
    ans += abs(a[l-1] - a[l] - val);
    ans += abs(a[r+1] - a[r] - val);

    if(localMax[l - 1]){
        if(a[l - 1] < (a[l] + val)){
            localMax[l-1] = 0;
            update3(0, n-1, 0, l-1);
        }
    }
    if(localMax[r + 1]){
        if(a[r+1] < (a[r] + val)){
            localMax[r+1] = 0;
            update3(0, n-1, 0, r+1);
        }
    }
    if(!localMax[r] && (a[r]) >= a[r-1] && (a[r] + val) >= a[r+1]){
        localMax[r] = 1;
        update3(0, n-1, 0, r);
    }
    if(!localMax[l] && (a[l] + val) >= a[l-1] && (a[l]) >= a[l+1]){
        localMax[l] = 1;
        update3(0, n-1, 0, l);
    }
    diff[l-1] -= val;
    update(0, n-1, 0, l-1);
    diff[r] += val;
    update(0, n-1, 0, r);

    frwdDiff[l] += val;
    update4(0, n-1, 0, l);
    frwdDiff[r+1] -= val;
    update4(0, n-1, 0, r+1);

    update2(0, n-1, 0, l, r, val);
}

int main(){
    ios_base :: sync_with_stdio(0);
    cin.tie(0);

    cin >> n;
    for(ll j = 0; j < n; j++)
        cin >> a[j];
    for(ll j = 0; j < n-1; j++){
        diff[j] = a[j] - a[j+1];
        ans += abs(diff[j]);
    }
    diff[n-1] = a[n-1];

    for(ll j = n-1; j > 0; j--)
        frwdDiff[j] = a[j] - a[j-1];
    frwdDiff[0] = a[0];

    for(ll j = 1; j < n-1; j++)
        if(a[j] >= a[j-1] && a[j] >= a[j+1]) localMax[j] = 1;

    build(0, n-1, 0);

    ll q;
    cin >> q;
    while(q--){
        ll t, l, r, val;
        cin >> t >> l >> r >> val;
        if(t == 1) cout << (ans + query_(l-1, r-1, val)) << endl;
        else update_(l-1, r-1, val);
        //cout << localMax[2] << " " << ans << endl;
    }
    return 0;
}

