using namespace std;
const int N = 1000010;
vector<int> primes;
bool tag[N] = {0};

long long get_primes(long long n)
{
    for (int i = 2;i < n;++i)
    {
        if (tag[i])
            continue;
        for (int j = i * 2;j < n;j += i)
            tag[j] = true;
    }
    for (int i = 2;i < n;++i)
        if (!tag[i])
            primes.push_back(i);
}

int main()
{
    get_primes(N);
    long long n, prime_count = primes.size();
    scanf("%I64d", &n);
    long long ans = 1;

    for (int i = 0;i < prime_count;++i)
    {
        if (n % primes[i] == 0)
            ans *= primes[i];
        while (n % primes[i] == 0)
            n /= primes[i];
    }
    ans *= n;
    printf("%I64d\n", ans);
}

