// Global variable for number of properties (p)
int p_global;


// Use a fixed-size struct to store a chemical's properties.
// Since p <= 10, we allocate an array of size 11 (extra space for safety).
struct Chem {
    int prop[11];
};


// Equality operator for Chem.
bool operator==(const Chem &a, const Chem &b) {
    for (int i = 0; i < p_global; i++) {
        if (a.prop[i] != b.prop[i])
            return false;
    }
    return true;
}


// Custom hash function for Chem.
struct ChemHash {
    size_t operator()(const Chem &c) const {
        size_t seed = 0;
        for (int i = 0; i < p_global; i++){
            // Combine the hash values for each property.
            seed ^= ( (size_t)c.prop[i] + 0x9e3779b9 + (seed << 6) + (seed >> 2) );
        }
        return seed;
    }
};


int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, p;
    cin >> n >> p;
    p_global = p;

    // Read the chemicals into a vector of Chem.
    vector<Chem> chemicals(n);
    for (int i = 0; i < n; i++){
        for (int j = 0; j < p; j++){
            cin >> chemicals[i].prop[j];
        }
    }

    // Read the required chemical.
    Chem req;
    for (int j = 0; j < p; j++){
        cin >> req.prop[j];
    }

    // 1. Check for an exact match.
    for (int i = 0; i < n; i++){
        if (chemicals[i] == req) {
            cout << i + 1 << endl;
            return 0;
        }
    }

    // 2. Build an unordered_map from a chemical to its index.
    // Reserve extra space to help reduce rehashing overhead.
    unordered_map<Chem, int, ChemHash> mp;
    mp.reserve(n * 2);
    for (int i = 0; i < n; i++){
        mp[chemicals[i]] = i;
    }

    // 3. For each chemical, compute its complement such that:
    //    complement[j] = req.prop[j] - chemicals[i].prop[j]
    // If a chemical matching the complement exists (and it is not the same chemical),
    // then mixing them yields the required chemical.
    for (int i = 0; i < n; i++){
        Chem comp;
        for (int j = 0; j < p; j++){
            comp.prop[j] = req.prop[j] - chemicals[i].prop[j];
        }
        auto it = mp.find(comp);
        if (it != mp.end()){
            int j = it->second;
            if (j != i) {  // ensure two distinct chemicals
                int a = min(i, j), b = max(i, j);
                cout << a + 1 << " " << b + 1 << endl;
                return 0;
            }
        }
    }

    // 4. If no valid chemical or pair is found, output "NO".
    cout << "NO" << endl;
    return 0;
}

