vector<int> memo;
unordered_map<int, int> table;

void compress(vector<pair<int, pair<int, int> > > &query) {
    for(auto &q : query) {
        memo.pb(q.second.first);
        memo.pb(q.second.second+1);
    }
    memo.pb(0);
    sort(memo.begin(), memo.end());
    memo.erase(unique(memo.begin(), memo.end()), memo.end());
    int pos = 0;
    for(int &x : memo) table[x] = pos++;
    for(auto &q : query) {
        q.second.first = table[q.second.first];
        q.second.second = table[q.second.second];
    }
}

struct node {
    bool all, none, flipped;
    int counter;
    node(bool a, bool b, bool c, int d) : all(a), none(b), flipped(c), counter(d) {}
};

void updateNode(vector<node> &segTree, int type, int lowerBound, int upperBound, int index) {//O(1)
    node &curr = segTree[index];
    if(type == 1) {//add all
        curr.all = true;
        curr.none = curr.flipped = false;
        curr.counter = memo[upperBound] - memo[lowerBound] + 1;
    } else if(type == 2) {//remove all
        curr.none = true;
        curr.all = curr.flipped = false;
        curr.counter = 0;
    }
}

void pushToChildren(vector<node> &segTree, int lowerBound, int upperBound, int index) {
    if(lowerBound == upperBound) return;
    node &curr = segTree[index];
    int mid = (lowerBound + upperBound)>>1;
    if(curr.all) {
        updateNode(segTree, 1, lowerBound, mid, 2*index);
        updateNode(segTree, 1, mid+1, upperBound, 2*index+1);
        curr.all = false;
    } else if(curr.none) {
        updateNode(segTree, 2, lowerBound, mid, 2*index);
        updateNode(segTree, 2, mid+1, upperBound, 2*index+1);
        curr.none = false;
    }
}

void update(vector<node> &segTree, int type, int l, int r, int lowerBound, int upperBound, int index) {
    if(lowerBound > upperBound || upperBound < l || r < lowerBound) return;
    node &curr = segTree[index];
    if(l <= lowerBound && upperBound <= r) {//range in query bounds
        updateNode(segTree, type, lowerBound, upperBound, index);
        return;
    }
    pushToChildren(segTree, lowerBound, upperBound, index);
    int mid = (lowerBound + upperBound)>>1;
    update(segTree, type, l, r, lowerBound, mid, 2*index);
    update(segTree, type, l, r, mid+1, upperBound, 2*index+1);
    curr.counter = segTree[2*index].counter + segTree[2*index+1].counter;
}

