void dij(int *dis,int u){
 static queue<int> q;
 static bool vis[maxn];
 for(int i=1;i<=n;i++)
  vis[i]=0,dis[i]=0;
 q.push(u);
 while(!q.empty()){
  u=q.front(),q.pop();
  vis[u]=1;
  for(int i=beg[u];i;i=nex[i]){
   if(!vis[tto[i]]){
    vis[tto[i]]=1;
    dis[tto[i]]=dis[u]+1;
