#define max(a, b) (a) > (b) ? (a) : (b)
#define min(a, b) (a) < (b) ? (a) : (b)
#define MAXN 200005
#define eps 1e-12
int n;
double a[MAXN], MX, tmpx, MN, tmpn;
bool Calc(double x)
{
    MX = MN = tmpn = tmpx = a[1] + x;
    for(int i = 2; i <= n; i ++) {
        tmpx = max(tmpx + a[i] + x, a[i] + x);
        MX = max(tmpx, MX);
        tmpn = min(tmpn + a[i] + x, a[i] + x);
        MN = min(tmpn, MN);
    }
    if(MN > eps) return 1;
    if(MX < eps) return 0;
    return fabs(MN) - MX < eps;
}
