struct node {
    int sum;
    node *n1, *n0;
    node() { sum = 0; n1 = n0 = NULL; }
};
int sum(node *x) { return x ? x->sum : 0; }

node *insert(node *root, int x, int i)
{
    if (!root) root = new node();
    if (i == -1) { ++root->sum; return root; }
    if (!hasbit(x, i)) root->n0 = insert(root->n0, x, i-1);
    else root->n1 = insert(root->n1, x, i-1);
    root->sum = sum(root->n0) + sum(root->n1);
    return root;
}

void erase(node *root, int x, int i)
{
    if (i == -1) { --root->sum; return; }
    if (!hasbit(x, i)) erase(root->n0, x, i-1);
    else erase(root->n1, x, i-1);
    root->sum = sum(root->n0) + sum(root->n1);
}

int best(node *root, int x, int i, int p)
{
    //W("calling at", x, i, p);
    if (i == -1) { return p; }
    if (hasbit(x, i)) return sum(root->n1) ? best(root->n1, x, i-1, setbit(p, i)) : best(root->n0, x, i-1, p);
    else return sum(root->n0) ? best(root->n0, x, i-1, p) : best(root->n1, x, i-1, setbit(p, i));
}

void print(node *x, string s="")
{
    if (!x) return;
    cout << s << ": " << x->sum << endl;
    print(x->n0, s + "L ");
    print(x->n1, s + "R ");
}

