const double eps = 0.00000005;
int n;
int a[12000];
double f[12000];
double calc(double x)
{
    f[1] = a[1] - x;
    double tmp = f[1];
    for (int i = 2; i <= n; i++)
    {
        if (f[i - 1] < 0) f[i] = a[i] -x;
        else f[i] = f[i - 1] + a[i] - x;
        if (f[i] > tmp) tmp = f[i];
    }
//    for (int i = 1; i <= n; i++)
//        cout << f[i] <<' ';

    f[1] = a[1] - x;
    if (-f[1] > tmp) tmp = -f[1];
    for (int i = 2; i <= n; i++)
    {
        if (f[i - 1] > 0) f[i] = a[i] -x;
        else f[i] = f[i - 1] + a[i] - x;
        if (-f[i] > tmp) tmp = -f[i];
    }
    return tmp;
}
