    double l = -maxn, r = maxn;
    double mid, midmid;
    double s1, s2, ans = 1.0e20;
    while(r - l >= eps)
    {
        mid = (l+r)/2;
        midmid = (mid+r)/2;
        s1 = calc(mid);
        s2 = calc(midmid);
        ans = min(ans, min(s1, s2));
        if(s1 < s2)
            r = midmid;
        else
        l = mid;
    }
