struct edge_t {
	int u, v, w;
	bool operator < (const edge_t& o) const {
		return w < o.w;
	}
	edge_t(int _u, int _v, int _w):
		u(_u), v(_v), w(_w) {}
};


const int MxN = 220;
int n, parent[MxN];
vector<edge_t> edges;


int find_root(int u) {
	if(u == parent[u]) {
		return u;
	}
	return parent[u] = find_root(parent[u]);
}


inline void solution(){
	int u, v, w, cnt = 0, sum = 0;
	cin >> u >> v >> w;
	edges.emplace_back(u, v, w);
	sort(edges.begin(), edges.end());
	for(auto x: edges) {
		int ru = find_root(x.u), rv = find_root(x.v);
		if(ru == rv) {
			continue;
		}
		cnt++;
		sum += x.w;
		parent[ru] = rv;
	}
	cout << (cnt == n - 1 ? sum: -1);
	return ;
}


