#include <cmath>

using namespace std;

#define ls (pos<<1)
#define rs (pos<<1|1)

const int maxn=1e5+20;
int flag;

struct Node
{
    int l,r,val;
    Node(){}
    Node(int ll,int rr,int vv):l(ll),r(rr),val(vv){}
    int mid()
    {
        return l+r>>1;
    }
}node[maxn<<2];

int gcd(int a,int b)
{
    return b ? gcd(b,a%b):a;
}

void maintain(int pos)
{
    if(node[pos].l!=node[pos].r)
        node[pos].val=gcd(node[ls].val,node[rs].val);
}

void build(int l,int r,int pos)
{
    node[pos]=Node(l,r,0);
    if(node[pos].l==node[pos].r)
    {
        scanf("%d",&node[pos].val);
    }
    else
    {
        int mid=l+r>>1;
        build(l,mid,ls);
        build(mid+1,r,rs);
        maintain(pos);
    }
}

void modify(int x,int val,int pos)
{
    if(node[pos].l==x&&node[pos].r==x)
    {
        node[pos].val=val;
        return ;
    }
    int mid=node[pos].mid();
    if(x<=mid)  modify(x,val,ls);
    else    if(x>mid)   modify(x,val,rs);
    maintain(pos);
}

void query(int l,int r,int val,int pos)
{
    if(flag>1)  return ;
    if(node[pos].val%val==0)    return ;

    if(l==node[pos].l&&r==node[pos].r)
    {
        if(node[pos].l==node[pos].r)
        {
            flag++;
            return ;
        }
        int mid=node[pos].mid();
        int flag_l=node[ls].val%val;
        int flag_r=node[rs].val%val;
        if(flag_l&&flag_r)  { flag=3; return ;}
        if(flag_l)
            query(l,mid,val,ls);
        if(flag_r)
            query(mid+1,r,val,rs);
        return ;
    }
    else
    {
        int mid=node[pos].mid();
        if(r<=mid)  query(l,r,val,ls);
        else    if(l>mid)   query(l,r,val,rs);
        else    query(l,mid,val,ls),query(mid+1,r,val,rs);
    }
}

int main()
{
    int n;
    while(scanf("%d",&n)!=EOF)
    {
        build(1,n,1);
        int q;
        scanf("%d",&q);
        while(q--)
        {
            int o;
            scanf("%d",&o);
            if(o==1)
            {
                int l,r,x;
                scanf("%d %d %d",&l,&r,&x);
                flag=0;
                query(l,r,x,1);
//                cout<<flag<<endl;
                if(flag<=1)     puts("YES");
                else            puts("NO");
            }
            else
            {
                int pos,val;
                scanf("%d %d",&pos,&val);
                modify(pos,val,1);
            }
        }
    }
    return 0;
}

