    dist2[e] = 0;


    for(int i = 0; i < M; i++) {
        ll a , b , c; cin >> a >> b >> c;
        edges[a].push_back({b,c});
        edges[b].push_back({a,c});
    }


    pq.push({s , 0});


    while(!pq.empty()) {
        ll x = pq.top().x;
        ll w = pq.top().w;
        pq.pop();
        if(visited1[x]) continue;
        visited1[x] = true;
        for(pair<int,ll> i : edges[x]) {
            if(dist1[i.first] > w + i.second) {
                dist1[i.first] = w + i.second;
                pq.push({i.first , dist1[i.first]});
            }
        }
    }


    if(p >= dist1[e]) {
