    pq.push({e , 0});


    while(!pq.empty()) {
        ll x = pq.top().x;
        ll w = pq.top().w;
        pq.pop();
        if(visited2[x]) continue;
        visited2[x] = true;
        for(pair<int,ll> i : edges[x]) {
            if(dist2[i.first] > w + i.second) {
                dist2[i.first] = w + i.second;
                pq.push({i.first , dist2[i.first]});
            }
        }
    }


    ll f1 = 0;
    ll f2 = 0;
