    for(int i = 1; i <= N; i++) {
        if(dist2[i] < f3 && dist1[i] <= p) {
            f1 = i;
            f2 = dist1[i];
            f3 = dist2[i];
        }
        else if (dist2[i] == f3 && dist1[i] > f2 && dist1[i] <= p) {
            f1 = i;
            f2 = dist1[i];
