int n;
string s;
bool dp[301][301][3];
int solve()
{
    cin >> n >> s;
    for( int i=0;i<n;i++ ) for( int j=0;j<n;j++ ) for( int k=0;k<3;k++ ) dp[i][j][k] = false;
    for( int i=0;i<n;i++ ) dp[i][i][ s[i]-'0' ] = true;
    for( int sz=2;sz<=n;sz++ )
    {
        for( int l=0;l+sz-1<n;l++ )
        {
            int r = l+sz-1;
            for( int mid=l;mid<r;mid++ )
            {
                dp[l][r][2] |= (dp[l][mid][0] && dp[mid+1][r][0]);
                dp[l][r][1] |= (dp[l][mid][0] && dp[mid+1][r][1]);
                dp[l][r][0] |= (dp[l][mid][0] && dp[mid+1][r][2]);
                dp[l][r][2] |= (dp[l][mid][1] && dp[mid+1][r][0]);
                dp[l][r][1] |= (dp[l][mid][1] && dp[mid+1][r][1]);
                dp[l][r][1] |= (dp[l][mid][1] && dp[mid+1][r][2]);
                dp[l][r][1] |= (dp[l][mid][2] && dp[mid+1][r][0]);
                dp[l][r][2] |= (dp[l][mid][2] && dp[mid+1][r][1]);
                dp[l][r][1] |= (dp[l][mid][2] && dp[mid+1][r][2]);
            }
        }
    }
    if( dp[0][n-1][0] ) cout << "yes\n";
    else cout << "no\n";
    return 0;
}
int main()
{
    ios_base::sync_with_stdio(0);cin.tie(0);
    int q = 20;
    while( q-- ) solve();
    return 0;
}

