#include <bits/stdc++.h>
using namespace std;
int n,m;
char table[1009][1009];
int mx=INT_MIN;
bool visited[1009][1009];
void flt(int x,int y){
	if(x<0||y<0||x>=n||y>=m)return;
	table[x][y]='#';
	if(table[x+1][y]=='.')flt(x+1,y);
	if(table[x-1][y]=='.')flt(x-1,y);
	if(table[x][y+1]=='.')flt(x,y+1);
	if(table[x][y-1]=='.')flt(x,y-1);
	return;
}
int recur(int x,int y){
	int ans=0;
	visited[x][y]=true;
	if(x-1>=0&&table[x-1][y]=='#')ans++;
	if(y-1>=0&&table[x][y-1]=='#')ans++;
	if(x+1<n&&table[x+1][y]=='#')ans++;
	if(y+1<m&&table[x][y+1]=='#')ans++;
	if(x-1>=0&&table[x-1][y]=='X'&&!visited[x-1][y])ans+=recur(x-1,y);
	if(y-1>=0&&table[x][y-1]=='X'&&!visited[x][y-1])ans+=recur(x,y-1);
	if(x+1<n&&table[x+1][y]=='X'&&!visited[x+1][y])ans+=recur(x+1,y);
	if(y+1<m&&table[x][y+1]=='X'&&!visited[x][y+1])ans+=recur(x,y+1);
	return ans;
}
int main(){
	ios_base::sync_with_stdio(false);cin.tie(0);
	cin >> n >> m;
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			cin >> table[i][j];
		}
	}
	flt(0,0);
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			if(table[i][j]=='X'&&visited[i][j]==false){
                //cout << i << " " << j << "\n";
				mx=max(mx,recur(i,j));
			}
		}
	}
	cout << mx;
	return 0;
}

