long long extgcd(long long a,long long b,long long& x,long long& y)
{
 if(!b)
 {
  x=1;y=0;return a;
 }
 long long d=extgcd(b,a%b,x,y);
 long long tmp=x;x=y;y=tmp-a/b*y;
 return d;
} 
long long China(int w[],int b[],int k)//a==b(mod w)
{
 long long x,y,a=0,m,n=1;
 for(int i=0;i<k;i++)n*=w[i];
 for(int i=0;i<k;i++)
 {
  m=n/w[i];
  extgcd((long long)w[i],m,x,y);
  a=(a+y*m*b[i])%n;
 }
 if(a>0)return a;else return a+n;
} 
