const ll mx = 2e5+1, Mod = 1e9+7;

int n, q, a[mx], seg[4*mx][101];
vector< pair<int, pii> > qtime[mx];

void build(int v, int S, int E)
{
    for(int i=1;i<=100;i++)
        seg[v][i] = i;
    if(E-S<2)
        return;
    int mid = (S+E)/2;
    build(2*v  , S, mid);
    build(2*v+1, mid, E);
}

void update(int v, int S, int E, int i, int x, int y)
{
    if(E-S<2)
    {
        seg[v][x]=y;
        return;
    }
    int mid = (S+E)/2;
    if(i<mid)
        update(2*v  , S, mid, i, x ,y);
    else
        update(2*v+1, mid, E, i, x, y);
    for(int i=1;i<=100;i++)
        seg[v][i] = seg[2*v+1][seg[2*v][i]];
}

int main()
{
    IOS;
    cin >> n;
    for(int i=0;i<n;i++)
        cin >> a[i];
    int l, r, x, y;
    cin >> q;
    build(1,0,q);
    for(int w=0;w<q;w++)
    {
        cin >> l >> r >> x >> y;
        qtime[l-1].pb(mp(w,mp(x,y)));
        qtime[r].pb(mp(w,mp(x,x)));
    }
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<qtime[i].size();j++)
            update(1, 0, q, qtime[i][j].X, qtime[i][j].Y.X, qtime[i][j].Y.Y);
        cout << seg[1][a[i]] << ' ';
    }
    return 0;
}


