const int SIZE = 1e6+10;
int dp[2][SIZE];
int get(int v,int l,int r){
    if(l>r)return 0;
    if(!l)return dp[v][r];
    return dp[v][r]-dp[v][l-1];
}
int main(){
    int n;R(n);
    VI a(n+1);
    FOR(i,1,n){
        R(a[i]);
        a[i]--;
        dp[a[i]][i]++;
    }
    REP(i,2)FOR(j,1,n){
        dp[i][j]+=dp[i][j-1];
    }
    int an=0;
    FOR(i,1,n){
        int ma1=0,ma2=0;
        FOR(j,0,i){
            ma1=max(ma1,get(0,1,j)+get(1,j+1,i));
        }
        FOR(j,i,n){
            ma2=max(ma2,get(0,i+1,j)+get(1,j+1,n));
        }
        an=max(an,ma1+ma2);
    }
    W(an);
    return 0;
}

