	T.clear();
	for(int i=0; i<=h; i++) T.push_back(i);

	int res = run_diagnostic(T);
	if(res >= 0){
		while(h > 0){
			h--;
			T.clear();
			for(int i=0; i<=h; i++) T.push_back(i);
			if(run_diagnostic(T)==-1){
				h++;
				break;
			}
		}
	} else {
