	}

	c[h] = 1;
	for(int i=0; i<h; i++){
		T.clear();
		for(int j=0; j<=h; j++){
			if(i!=j) T.push_back(j);
		}

		if(run_diagnostic(T)==-1) c[i] = 1;
	}

	h--;
	for(int i=h+2; i<N; i++){
		T.clear();
		for(int j=h+1; j<N; j++){
			if(i!=j) T.push_back(j);
		}

		if(run_diagnostic(T)<=0) c[i] = 1;
	}

	return c;
}

