#include <iomanip>

using namespace std;

int main()
{
 long long int k, d, t, time = 0;
 cin >> k >> d >> t;
 k *= 2; d *= 2; t *= 2;
 long long int hot, warm;
 if (k >= d)
 {
  hot = k;
  warm = d - k % d;
  if (warm == d)
   warm = 0;
  time += t / (hot + warm / 2) * (hot + warm);
  t %= (hot + warm / 2);
  if (t > k)
  {
   time += k;
   t -= k;
   time += t * 2;
  }
  else
  {
   time += t;
  }
 }
 else
 {
  hot = k;
  warm = d - k;
  time += t / (hot + warm / 2) * (hot + warm);
  t %= (hot + warm / 2);

  if (t > k)
  {
   time += k;
   t -= k;
   time += t * 2;
  }
  else
  {
   time += t;
  }
 }
 cout << setprecision(18)<<(double)time / 2 << endl;

 //system("PAUSE");
 return 0;
}

