set<ll> ms;
set<ll> :: iterator it, it1;
ll dp[1000010], n, m, a[1000010], fen[1000010];

void preCal()
{
    up(i,1,1000000) {
        for (ll j=i; j<= 1000000; j+= i) dp[j]++;
    }
}

void update(ll idx, ll val)
{
    while (idx <= n) {
        fen[idx] += val;
        idx += idx & (-idx);
    }
}

ll query(ll idx)
{
    ll sum = 0;
    while (idx > 0) {
        sum += fen[idx];
        idx -= idx & (-idx);
    }
    return sum;
}

int main()
{
    ll type,l,r, tmp;
    ios_base::sync_with_stdio(0);

    preCal();
    cin >> n >> m;
    up(i,1,n) {
        cin >> a[i];
        update(i,a[i]);
        ms.ins(i);
    }

    up(i,1,m) {
        cin >> type >> l >> r;
        if (type == 1) {
            it = ms.lower_bound(l);
            while (it!= ms.end() && (*it) <= r) {
                tmp = a[*it];
                update(*it,-tmp);
                update(*it, dp[tmp]);
                a[*it] = dp[tmp];
                it1 = it;
                it++;
                if (dp[tmp] <= 2) ms.erase(it1);
            }
        }
        else {
            printf("%lld\n",query(r) - query(l-1));
        }
    }
}

