using namespace std;
int E[100001],P[20000],n,m,nr,sum,poz,nrc,i,j;

void e(int n)
{
    int i,j;
    for(i=1; ((i*i)<<1)+(i<<1)<=n; i++)
    {
        if(E[i]==0)
        {
            for (j=((i*i)<<1)+(i<<1); (j<<1)+1<=n; j+=(i<<1)+1)
            {
                E[j]=1;
            }
        }
    }
    for(i=1; 2*i+1<=n; i++)
    {
        if(E[i]==0)
            P[++nr]=2*i+1;
    }
}

int bs(int st, int dr, int val)
{
    int mij=st+(dr-st)/2;
    if(st>=dr)
    {
        if(P[mij]<val)
            mij++;
        return mij;
    }
    if(P[mij]<val)
    {
        return bs(mij+1,dr,val);
    }
    else
    {
        return bs(st,mij,val);
    }
}

int main()
{
    ios::sync_with_stdio(false);
    P[1]=2;
    nr=1;
    cin>>n>>m;
    e(n+1000);
    sum=n-1;
    poz=bs(1,nr,sum);
    cout<<P[poz]<<" "<<P[poz]<<"\n";
    for(i=1; i<=n-2; i++)
        cout<<i<<" "<<i+1<<" 1\n";
