    nrc=m-n+1;
    for(i=1; i<=n; i++)
    {
        if(nrc==0)
            break;
        for(j=i+2; j<=n; j++)
        {
            cout<<i<<" "<<j<<" "<<1000000000<<"\n";
            nrc--;
            if(nrc==0)
                break;
        }
        if(nrc==0)
            break;
    }
    return 0;
}

