   else b += Y;

   int c = a[i] / Y;
   if (a[i] % Y != 0)c += a[i] % Y + 1;
   else c += Y;

   b -= c;
   if (b < 0)b = -b;
   if (b > 1) {
    OK = 0;
    break;
   }
  }
 }
