struct Tree {
    ll x, add;
    Tree() {
        x = 0;
        add = 0;
    }
} t[2][MaxN];
ll ans;

void push (int x, int v, int l, int r) {
    if (t[x][v].add) {
        t[x][v].x += t[x][v].add * (r - l + 1);
        t[x][v + v].add += t[x][v].add;
        t[x][v + v + 1].add += t[x][v].add;
        t[x][v].add = 0;
    }
}
void upd2 (int v, int l, int r, int pos, int val) {
    if (l == r) {
        t[1][v].x = val;
        return;
    }
    int m = (l + r) >> 1;
    if (pos <= m)
        upd2 (v + v, l, m, pos, val);
    else
        upd2 (v + v + 1, m + 1, r, pos, val);
    t[1][v].x = t[1][v + v].x + t[1][v + v + 1].x;
}

void upd (int x, int v, int l, int r, int L, int R, int val) {
    push(x, v, l, r);
    if (l > R || r < L)
        return;
    if (L <= l && r <= R) {
        t[x][v].add += val;
        push(x, v, l, r);
        return;
    }
    int m = (l + r) >> 1;
    upd (x, v + v, l, m, L, R, val);
    upd (x, v + v + 1, m + 1, r, L, R, val);
    t[x][v].x = t[x][v + v].x + t[x][v + v + 1].x;
}
ll get (int x, int v, int l, int r, int L, int R) {
    push (x, v, l, r);
    if (L <= l && r <= R) {
        return t[x][v].x;
    }
    if (l > R || r < L)
        return 0;
    int m = (l + r) >> 1;
    return get (x, v + v, l, m, L, R) + get (x, v + v + 1, m + 1, r, L, R);
}
void build (int v, int l, int r) {
    if (l == r) {
        t[0][v].x = a[l];
        if (a[l] >= a[l - 1] && a[l] >= a[l + 1])
            t[1][v].x = 1;
        return;
    }
    int m = (l + r) >> 1;
    build (v + v, l, m);
    build (v + v + 1, m + 1, r);
    t[1][v].x = t[1][v + v].x + t[1][v + v + 1].x;
    t[0][v].x = t[0][v + v].x + t[0][v + v + 1].x;
}
void getP (int l, int xx, int q) {
    ll x = 0;
    if (l + q >= 1 && l + q <= n)
        x = get (0, 1, 1, n, l + q, l + q);
    ll y = get (0, 1, 1, n, l, l);
//    cout << l << ' ' << x << ' ' << y << endl;
    ans += abs (x - y) * xx;
}

void uppp (int l) {
    if (!l)
        return;
    if (l > n)
        return;
    ll l1 = 0;
    ll r1 = 0;
    if (l != 1) {
        l1 = get (0, 1, 1, n, l - 1, l - 1);
    }
    if (l != n) {
        r1 = get (0, 1, 1, n, l + 1, l + 1);
    }
    ll x = get (0, 1, 1, n, l, l);
    if (x >= l1 && x >= r1)
        upd2 (1, 1, n, l, 1);
    else
        upd2 (1, 1, n, l, 0);
}
void updx (int l, int r, int x) {
    getP (l, -1, -1);
    getP (r, -1, 1);
    upd (0, 1, 1, n, l, r, x);
    getP (l, 1, -1);
    getP (r, 1, 1);
    uppp (l - 1);
    uppp (l);
    uppp (l + 1);
    uppp (r - 1);
    uppp (r);
    uppp (r + 1);
}
void updy (int l, int r, int x) {
    getP (l, -1, -1);
    getP (r, -1, 1);
    upd (0, 1, 1, n, l, r, x);
    getP (l, 1, -1);
    getP (r, 1, 1);
}
