using namespace std;
#include <bits/stdc++.h>

#define ll long long

#define pb push_back

int main() {
 string a;
 cin >> a;
 set<int> z,u;
 for(int i = 0; i < a.size(); i++){
  if(a[i] == '1') u.insert(i+1);
  else z.insert(i+1);
 }
 if(!u.size()){
  printf("%d\n",(int)a.size());
  for(int i = 0; i < a.size(); i++){
   printf("1 %d\n",i+1);
  }
  return 0;
 }
 vector< vector<int> > ans;
 for(int i = 0; i < a.size(); i++){
  int pos = i+1;
  if(a[i] != '0' or !z.count(pos)) continue;
  vector<int> aux;
  aux.pb(pos);
  z.erase(pos);
  while(1){
   if(!z.size()) break;
   auto um = u.upper_bound(pos);
   if(um == u.end()) break;
   auto zero = z.upper_bound(*um);
   if(zero == z.end()) break;
   aux.pb(*um);
   aux.pb(*zero);
   u.erase(*um);
   z.erase(*zero);
   pos = *zero;
  }
  ans.pb(aux);
 }
 if(!ans.size() or z.size() or u.size()) return puts("-1"),0;
 printf("%d\n",(int)ans.size());
 for(auto x: ans){
  printf("%d ",(int)x.size());
  for(auto y: x) printf("%d ",y);
  puts("");
 }

}
